_type(self, sql_statement):&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        pattern = re.compile(r&amp;#x27;\b(insert|delete|update|select)\b&amp;#x27;, re.IGNORECASE)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        match = pattern.search(sql_statement)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        if match:&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;            return match.group(1).upper()&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        return None&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    def extract_table_name_from_sql(self, sql_statement):&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        pattern = re.compile(r&amp;#x27;\bFROM\b\s+([^\s;]+)|\bJOIN\b\s+([^\s;]+)|\bUPDATE\b\s+([^\s;]+)|\bINTO\b\s+([^\s;]+)|\bMERGE\b\s+([^\s;]+)|\bTABLE\b\s+([^\s;]+)&amp;#x27;, re.IGNORECASE)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        match = pattern.search(sql_statement)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        if match:&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;            return next(group for group in match.groups() if group is not None)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        return None&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    def extract_user_list(self):&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        user_file = open(self.users_file, encoding=&amp;#x27;utf-8&amp;#x27;)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        content = user_file.read()&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        users_list = 