          tpermissions = self.permissions_list[int(tuser[3]) - 1]&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;                sql_head = tpermissions[2]&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;                table_pass = tpermissions[3]&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;                table_name = self.extract_table_name_from_sql(values[3])&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;                ttable = self.tables[self.table_dict[table_name] - 1]&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;                if str(self.table_dict[table_name]) in table_pass:&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;                    sql_type = self.extract_sql_operation_type(values[3]).lower()&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;                    if sql_type in sql_head:&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;                        time_ranges = [item.replace(&amp;#x27;&amp;quot;&amp;#x27;, &amp;#x27;&amp;#x27;) for item in ttable[2:]]&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;                        datetime_object = datetime.strptime(values[2], &amp;#x27;%Y/%m/%d %H:%M:%S&amp;#x27;)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;                        time_only = datetime_object.time()&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;                        if self.is_time_in_ranges(str(time_only), time_ranges):&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;                            pass&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;                        else:&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;                            son = tuser[0] + &amp;#x27;_&amp;#x27; + tpermissions[0] + &amp;#x27;_&amp;#x27; + str(self.table_dict[table_name]) + &amp;#x27;_&amp;#x27; + values[0]&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;                            result_flags.append(son)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;                    else:&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;                        son = tuser[0] + &amp;#x27;_&amp;#x27; + tpermissions[0] + &amp;#x27;_&amp;#x27; + str(self.table_dict[table_name]) + &amp;#x27;_&amp;#x27; + values[0]&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;                        result_flags.append(son)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;                else:&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;                    son = tuser[0] + &amp;#x27;_&amp;#x27; + tpermissions[0] + &amp;#x27;_&amp;#x27; + str(self.table_dict[table_name]) + &amp;#x27;_&amp;#x27; + values[0]&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;                    result_flags.append(son)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;                ttable = self.tables[self.table_dict[table_name] - 1]&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;            else:&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;                son = &amp;#x27;0_0_0_&amp;#x27; + values[0]&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;                result_flags.append(son)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        return result_flags&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    def custom_sort(self, item):&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        p = item.split(&amp;#x27;_&amp;#x27;)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        return tuple(map(int, p))&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    def print_sorted_list(self, sorted_list):&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        for i in sorted_list:&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;            print(i, end=&amp;#x27;&amp;#x27;)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;            print(&amp;#x27;,&amp;#x27;, end=&amp;#x27;&amp;#x27;)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    def main(self):&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        result_flags = self.process_actions()&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        sorted_list = sorted(result_flags, key=self.custom_sort)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        self.print_sorted_list(sorted_list)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;if __name__ == &amp;quot;__main__&amp;quot;:&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    log_processor = LogProcessor()&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    log_processor.main()&lt;/span&gt;&lt;br&gt;&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;/figure&gt;
