lass=&#34;line&#34;&gt;draw = ImageDraw.Draw(new_image)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;def bold_pixel(x, y):&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    for i in range(x - 1, x + 2):&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        for j in range(y - 1, y + 2):&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;            draw.point((i, j), fill=(0, 0, 0))&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;for x in range(width):&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    for y in range(height):&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        pixel = image.getpixel((x, y))&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        if all(value &amp;gt; threshold for value in pixel):&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;            bold_pixel(x, y)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;new_image.save(&amp;#x27;output_image.png&amp;#x27;)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;image.close()&lt;/span&gt;&lt;br&gt;&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;/figure&gt;
