;&gt;def write():&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    while True:&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        r = session.post(url1, data=data1, files=file, cookies=cookies)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;def read():&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    while True:&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        r = session.get(url2)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        if &amp;#x27;flag&amp;#x27; in r.text:&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;            print(r.text)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;threads = [threading.Thread(target=write),&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;           threading.Thread(target=read)]&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;for t in threads:&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    t.start()&lt;/span&gt;&lt;br&gt;&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;/figure&gt;
